CREATE TABLE IF NOT EXISTS `lib_article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '文档ID',
  `parse` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '内容解析类型',
  `type` int(1) DEFAULT NULL COMMENT '文章分类',
  `title` varchar(60) NOT NULL COMMENT '标题',
  `summary` varchar(250) DEFAULT NULL COMMENT '简介',
  `content` text NOT NULL COMMENT '文章内容',
  `template` varchar(100) DEFAULT NULL COMMENT '详情页显示模板',
  `bookmark` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '收藏数',
  `createtime` int(11) NOT NULL COMMENT '创建时间',
  `status` tinyint(4) DEFAULT NULL COMMENT '状态',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='文章表' AUTO_INCREMENT=24 ;

