-- 转存表中的数据 `lib_article`
--

INSERT INTO `lib_article` (`id`, `parse`, `type`, `title`, `summary`, `content`, `template`, `bookmark`, `createtime`, `status`) VALUES
(1, 0, 0, '', '', '<h1>\n	OneThink1.1开发版发布&nbsp;\n</h1>\n<p>\n	<br />\n</p>\n<p>\n	<strong>OneThink是一个开源的内容管理框架，基于最新的ThinkPHP3.2版本开发，提供更方便、更安全的WEB应用开发体验，采用了全新的架构设计和命名空间机制，融合了模块化、驱动化和插件化的设计理念于一体，开启了国内WEB应用傻瓜式开发的新潮流。&nbsp;</strong> \n</p>\n<h2>\n	主要特性：\n</h2>\n<p>\n	1. 基于ThinkPHP最新3.2版本。\n</p>\n<p>\n	2. 模块化：全新的架构和模块化的开发机制，便于灵活扩展和二次开发。&nbsp;\n</p>\n<p>\n	3. 文档模型/分类体系：通过和文档模型绑定，以及不同的文档类型，不同分类可以实现差异化的功能，轻松实现诸如资讯、下载、讨论和图片等功能。\n</p>\n<p>\n	4. 开源免费：OneThink遵循Apache2开源协议,免费提供使用。&nbsp;\n</p>\n<p>\n	5. 用户行为：支持自定义用户行为，可以对单个用户或者群体用户的行为进行记录及分享，为您的运营决策提供有效参考数据。\n</p>\n<p>\n	6. 云端部署：通过驱动的方式可以轻松支持平台的部署，让您的网站无缝迁移，内置已经支持SAE和BAE3.0。\n</p>\n<p>\n	7. 云服务支持：即将启动支持云存储、云安全、云过滤和云统计等服务，更多贴心的服务让您的网站更安心。\n</p>\n<p>\n	8. 安全稳健：提供稳健的安全策略，包括备份恢复、容错、防止恶意攻击登录，网页防篡改等多项安全管理功能，保证系统安全，可靠、稳定的运行。&nbsp;\n</p>\n<p>\n	9. 应用仓库：官方应用仓库拥有大量来自第三方插件和应用模块、模板主题，有众多来自开源社区的贡献，让您的网站“One”美无缺。&nbsp;\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong>&nbsp;OneThink集成了一个完善的后台管理体系和前台模板标签系统，让你轻松管理数据和进行前台网站的标签式开发。&nbsp;</strong> \n</p>\n<p>\n	<br />\n</p>\n<h2>\n	后台主要功能：\n</h2>\n<p>\n	1. 用户Passport系统\n</p>\n<p>\n	2. 配置管理系统&nbsp;\n</p>\n<p>\n	3. 权限控制系统\n</p>\n<p>\n	4. 后台建模系统&nbsp;\n</p>\n<p>\n	5. 多级分类系统&nbsp;\n</p>\n<p>\n	6. 用户行为系统&nbsp;\n</p>\n<p>\n	7. 钩子和插件系统\n</p>\n<p>\n	8. 系统日志系统&nbsp;\n</p>\n<p>\n	9. 数据备份和还原\n</p>\n<p>\n	<br />\n</p>\n<p>\n	&nbsp;[ 官方下载：&nbsp;<a href="http://www.onethink.cn/download.html" target="_blank">http://www.onethink.cn/download.html</a>&nbsp;&nbsp;开发手册：<a href="http://document.onethink.cn/" target="_blank">http://document.onethink.cn/</a>&nbsp;]&nbsp;\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<strong>OneThink开发团队 2013~2014</strong> \n</p>', '', 0, 0, 0),
(2, 0, 7, 'SCI数据库讲座', 'SCI数据库讲座SCI数据库讲座SCI数据库讲座', '&lt;p&gt;这是我们图书馆 第一次邀请大学者&amp;nbsp;&lt;/p&gt;', '', 0, 1460278205, 0),
(3, 0, 4, '馆藏布局', '馆藏布局', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-family:黑体&quot;&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;图书（纸质）部分&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;1、可外借图书：&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;可供外借的图书分藏于五个库室，包括从一楼借还书入口处的四个书库及位于二楼210房间的文学书库。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;各书库藏书种类如下：&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;⑴一楼书库：&lt;br/&gt;&lt;/strong&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;主要是工业技术类图书（分类号为T大类），包括各种工业用标准、机械工业、金属工艺、电子、电信技术、计算机技术、化工、建筑&lt;br/&gt;&lt;br/&gt;科学等。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;⑵ 二楼书库：&lt;br/&gt;&lt;/strong&gt;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;从内楼梯上去，首先进入二楼南书库，主要包括语言、艺术、历史、地理、综合性类图书（图书类号为H、J、K、Z）以及外文原版&lt;br/&gt;&lt;br/&gt;书；&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;从二楼南书库径直穿过，进入二楼北书库，主要包括自然科学总论、数理化、天文、生物、医药卫生、农业、交通运输、航空、航&lt;br/&gt;&lt;br/&gt;天、环境科学等图书（图书类号为N、O、P、Q、R、S、U、V、X）。&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;⑶ 三楼书库：&lt;/strong&gt;&amp;nbsp;&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;从内楼梯上去，进入三楼书库。主要包括马、恩、列、毛、邓等的思想理论研究及著作、各类哲学流派、政治、法律、军事、经济、&lt;br/&gt;&lt;br/&gt;文化、教育、体育类图书（图书类号为A、B、C、D、E、F、G）。&amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;⑷ 文学库：&lt;br/&gt;&lt;/strong&gt;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;位于二楼210房间，包括各种文学理论、文学创作、中外文学名著、各类文学著作等（图书类号为I大类）。&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;以上书库均实行开架借阅。&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;2、只限于阅览的图书&lt;br/&gt;&lt;/strong&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;⑴文献检索室&lt;/strong&gt;（227房间）：&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;包括各类检索工具书，如中外文书目、索引。一般工具书包括字典、词典、图册、手册、年鉴、百科全书、指南、机构名录等。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;⑵教师阅览室&lt;/strong&gt;（202房间）：&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;主要针对教师读者提供知识深度较高、使用价值较大的图书，一般在图书馆的复本量只为一本。共有藏书二万余册。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;⑶学生阅览室&lt;/strong&gt;（143房间）：&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;主要针对学生读者提供复本量较少而需求量较大、限于经费有限不能大量采购的图书。藏书量为一万五千余册。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;⑷艺术阅览室&lt;/strong&gt;（位于学生阅览室对面的101房间）：&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;收藏艺术类中文图书7000余册，英文原版图书400余册，日文200余册。艺术阅览室是我馆的特色馆藏。&lt;br/&gt;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;⑸古籍阅览室&lt;/strong&gt;（339房间）:&amp;nbsp;&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;该阅览室收藏了大连大学图书馆五十多年来艰辛积累的一万五千多册古籍，其特点是我国古代重要典籍比较齐全，如《四库全书》、&lt;br/&gt;&lt;br/&gt;《续修四库全书》、《古今图书集成》、《永乐大典》、《宛委别藏》、《唐开成石壁十三经》等巨著，基本包揽了我国现存的主要古&lt;br/&gt;&lt;br/&gt;籍，另外，一定数量极其珍贵的宋版、明版和善本书也有收藏。&amp;nbsp; 古籍的分类是继承历代目录学的传统，按经、史、子、集四部分类的。&lt;br/&gt;&lt;br/&gt;为方便读者利用，图书馆编印了《大连大学图书馆馆藏古籍书目》。古籍阅览室是我馆的特色馆藏。&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;⑹东芝阅览室&lt;/strong&gt;（230房间）：&amp;nbsp;&amp;nbsp;&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;是日本东芝大连公司赠建的全日文原版图书，计有图书1500余册，另有金沢文库藏书2000余册。&lt;br/&gt;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;⑺外文阅览室&lt;/strong&gt;（212房间）：&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&lt;strong&gt;&lt;span style=&quot;font-size:12px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;全部文献为外文原版，计有英文图书近3400册，外文期刊有83种原版刊，英文版国内期刊4种。&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460295771, 1),
(4, 0, 4, '借阅规则', '借阅规则', '&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong&gt;一、借书证管理规则&lt;/strong&gt;&amp;nbsp;&lt;br/&gt;&lt;br/&gt;&amp;nbsp; 1、如何办理借书证&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;①学校正式教职工凭工作证到图书馆办理。办证时每人须交本人近期免冠半身一寸照片一张。&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;②学生以班级为单位到图书馆集中办理借书证。&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&amp;nbsp; 2、丢失借书证如何挂失及补办&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;读者遗失借书证后，应立即到图书馆办证处挂失并申请补办手续，补办需按规定交纳工本费10元。挂失前所有被借图书由图书证持有&lt;br/&gt;&lt;br/&gt;人承担责任。&lt;br/&gt;&lt;br/&gt;&amp;nbsp; 3、违规处罚的有关规定&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 借用他人借书证者，一经查出对借用和被借用双方均处以罚款并冻结借书权利一个月以上。&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 使用本馆已废止或注销的借书证、假冒借书证、别人已挂失的借书证等，视情节轻重进行相应的处罚。未办理借书手续、私自携书离&lt;br/&gt;&lt;br/&gt;开本馆者，一律按照偷窃图书论处。发现，将从重处罚，并上报有关部门。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;strong&gt;二、书刊借阅程序及相关制度&lt;br/&gt;&lt;/strong&gt;&lt;br/&gt;&amp;nbsp; 1、读者必须出示本人借阅证方可进入阅览室，并在读者签名簿上签名（主要是为以后图书馆做统计用，自习&amp;nbsp; 室除外）。过刊阅览室、&lt;br/&gt;&lt;br/&gt;学生阅览室、外文阅览室须押证入室。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 2、读者入阅览室除可携带笔记本外，自己的书刊及书包等一律不得带入阅览室。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 3、读者进入阅览室不得穿大衣，随身携带的贵重物品请自行妥善保管好。&lt;br/&gt;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp; 4、读者取阅图书期刊各限一册，报纸一份，阅览完毕放回原处，不得乱放，不准带出室外。如需复印，须向老师声明，并押证登记。外&lt;br/&gt;&lt;br/&gt;文、过刊阅览室与学生阅览室使用代书板。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 5、爱护书刊、爱护设备，不准在书刊及桌椅上乱划，禁止撕页、折页等。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 6、保持肃静，注意卫生，禁止吐痰、吸烟，不准乱扔杂物。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 7、借阅制度&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;①借书权限：&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;每人6册，小说只可借2册，如不借小说，可借其他书库图书6册，可循环使用；一等奖学金和优秀新生奖学金获得者以及教改实验班&lt;br/&gt;&lt;br/&gt;学生的借书权限为8册，其中小说的借书权限仍为2册；&lt;span style=&quot;font-size: 12px;font-family: 宋体&quot;&gt;硕士研究生的借书权限为10册，对书籍类别没有限制。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;②借书期限：&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;借书期限是一个月，除文学书外，&lt;span style=&quot;font-size: 12px;color: rgb(51, 51, 51);font-family: 宋体&quot;&gt;其他图书可在到期前一周内续借，&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体&quot;&gt;学生可续借&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: &amp;#39;Times New Roman&amp;#39;&quot;&gt;2&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体&quot;&gt;次；教师可续借&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: &amp;#39;Times New Roman&amp;#39;&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;font-size: 12px;font-family: 宋体&quot;&gt;次。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;③超期罚款：&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;每超一天罚款0.10元，罚款金额由计算机自动累计。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;④爱护图书：&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;不得污损、圈点、划线、折页、批注等，借书前要仔细检查所借图书，如有上述情况，应在书上加盖破损印。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;⑤读者有过期图书未归还或未交纳超期罚款，均不得再借阅图书。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;⑥读者入库不得吸烟，不得携带易燃、易爆物品。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ⑦不得带书刊或书包入库。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ⑧书库内不得喧哗、吃东西、吐痰等。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 8、借阅证的使用及办证制度&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;① 读者须爱护借阅证，如果借阅证严重破损（如侧面开胶、照片脱落）或光照器不能识别其条形码，借阅证均被作废。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;② 读者遗失借阅证须及时到借还书处办理挂失手续，否则造成的后果由本人自负，如需补办借阅证，须在每月第一周到借还书处办&lt;br/&gt;&lt;br/&gt;理。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;strong&gt;三、书刊赔偿制度&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 1、读者严重损坏图书或遗失图书，可以赔偿同样图书或按以下规定赔偿：&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ①一般中文图书按书原价5倍赔偿，小说按3倍赔偿，不足5.00元按原价5.00元处理。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ②丢失成套书刊中的一册，按成套书刊价格的5倍赔偿。&lt;br/&gt;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ③外文原版书按20倍赔偿。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ④损毁线装书、善本书等特许借阅的图书按30倍赔偿。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 2、遗失单张报纸者，按全月合订本价格赔偿，遗失合订本报纸者，按合订本价格的5～10倍赔偿。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 3、 损坏图书、期刊视情节支付赔偿。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ①损坏图书、期刊程度较轻者，不影响书刊内容完整的，按书刊价格的20%～1倍赔偿。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;②损坏书刊较严重者，必须经过加工整修方能流通者，按原价赔偿，并付加工费。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ③读者剪裁、撕割书刊者，一律按偷书处理。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 4、 读者偷窃书刊者，一经发现，即处以原价10倍以上罚款，并张榜示名，取消借阅资格，通报所在单位及学校从严处理。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;四、网络电子资源使用规则&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 1、遵守知识产权法，尊重电子资源数据商的版权利益，按规定正确使用大连大学图书馆的电子资源。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 2、&lt;span style=&quot;font-family:宋体&quot;&gt;严禁私设代理服务器非法使用大连大学图书馆的电子资源。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; 3、&lt;span style=&quot;font-family:宋体&quot;&gt;严禁利用网络下载工具等非正常阅读使用、批量下载大连大学图书馆的电子资源。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong&gt;&amp;nbsp;五、上网阅览规则&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp; （一）不得制作、下载、复制、查阅、发布、传播或者以其他方式使用含有下列内容的信息：&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　1、反对宪法确定的基本原则的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　2、危害国家统一、主权和领土完整的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　3、泄露国家秘密，危害国家安全或者损害国家荣誉和利益的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　4、煽动民族仇恨、民族歧视，破坏民族团结，或者侵害民族风俗、习惯的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　5、破坏国家宗教政策，宣扬邪教、迷信的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　6、散布谣言，扰乱社会秩序，破坏社会稳定的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　7、宣传淫秽、赌博、暴力或者教唆犯罪的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　8、侮辱或者诽谤他人，侵害他人合法权益的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　9、危害社会公德或者民族优秀文化传统的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　10、含有法律、行政法规禁止的其他内容的。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　（二）不得进行下列危害信息网络安全的活动：&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　1、故意制作或者传播计算机病毒以及其他破坏性程序的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　2、非法侵入计算机信息系统或者破坏计算机信息系统功能、数据和应用程序的；&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;　　3、私自拆卸机器硬件设备、私自删除软件及修改设置。&lt;/p&gt;&lt;p style=&quot;padding: 0px;margin-top: 0px;margin-bottom: 0px;color: rgb(85, 85, 85);font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif;font-size: 14px;line-height: 30px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;4、进行法律、行政法规禁止的其他活动的。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460295927, 1),
(5, 0, 4, '图书馆辅楼开馆时间', '图书馆辅楼开馆时间', '&lt;p&gt;周一至周五8:10-21:40&amp;nbsp;&lt;/p&gt;&lt;p&gt;周六、日8:50-21:40&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460295994, 1),
(6, 0, 4, '关于新入校教职工启用图书馆功能的流程', '关于新入校教职工启用图书馆功能的流程', '&lt;p&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; background-color: rgb(255, 255, 255);&quot;&gt;请在学校人事处和网络信息中心办理相关手续后，到图书馆一楼总服务台进行图书借阅信息的完善，即可利用图书馆各项服务功能。具体图书借阅相关规定等请见图书馆主页&lt;/span&gt;&lt;/p&gt;', NULL, 0, 1460296047, 1),
(7, 0, 5, '学习考试类数据库检索与利用讲座', '图书馆开的小灶', '&lt;p style=&quot;padding: 0px; margin: 0cm 0cm 0pt 36pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;strong&gt;&amp;nbsp;图书馆开的小灶，你造吗？&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center;padding: 0px; margin: 0cm 0cm 0pt 36pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 0pt 36pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span face=&quot;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size: 16px;&quot;&gt;(&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;小灶主题&lt;span face=&quot;&quot;&gt;:&lt;/span&gt;学习考试类数据库检索与利用讲座&lt;span face=&quot;&quot;&gt;)&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 0pt 36pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 1、环球英语多媒体数据库&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 0pt 36pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 2、中科VIPExam考试学习数据库&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 0pt 36pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 3、中科UMajor大学专业课数据库&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 0pt 36pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 4、四级网考系统&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 0pt 36pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 5、维普考试服务平台&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 0pt 36pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 28pt; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 实话实说，是不是不造上面都是些什么鬼？不知道没关系，圕圕告诉你，这&amp;nbsp; 是咱们图书馆现有的考试数据库资源。像英语太差呀，四级没过呀，国二没过呀，专业课没学好呀，图书馆都可&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 以&amp;nbsp;补小灶哒，变身成学霸，快来快来吧~&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;strong&gt;时间：2016年4月1日（星期五）下午13：30&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;strong&gt;地点：图书馆辅楼二楼多媒体电子阅览区&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;主讲人：杨克香&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;讲座对象：全体师生（来者不拒）&lt;/p&gt;', NULL, 0, 1460296166, 1);
INSERT INTO `lib_article` (`id`, `parse`, `type`, `title`, `summary`, `content`, `template`, `bookmark`, `createtime`, `status`) VALUES
(8, 0, 5, '《红楼梦》中宝黛爱情最大悲剧是什么', '《红楼梦》中宝黛爱情最大悲剧是什么', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;【“博雅讲坛”——第一期.第一场】&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 《红楼梦》中宝黛爱情最大悲剧是什么&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;strong&gt;主讲人：高日晖教授&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;时间：2016年4月6日17：00—18：00（星期三）&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;地点：图书馆报告厅(主楼103)&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; “满纸荒唐言，一把辛酸泪。都云作者痴，谁解其中味？&amp;quot;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 一本《红楼梦》，一幕爱情与婚姻的悲剧，一部人生的悲剧。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;宝黛“悲金悼玉”的爱情悲剧，历来让读者为之断肠。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;在这“千红一哭”、“万艳同悲”的“女儿国”悲剧中，&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;宝黛爱情的最大悲剧是什么？&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;让我们跟着高日晖教授独特的视野，&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;走进《红楼梦》&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;去感受鲁迅所言的“悲凉之雾，遍被华林。然呼吸而领会之者，独宝玉而已”&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460296417, 1),
(9, 0, 5, 'JSTOR西文过刊数据库检索方法与技巧', 'JSTOR西文过刊数据库检索方法与技巧', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;时间：&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;strong&gt;&lt;span face=&quot;&quot;&gt;2016&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;年&lt;/strong&gt;&lt;strong&gt;&lt;span face=&quot;&quot;&gt;4&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;月&lt;/strong&gt;&lt;strong&gt;&lt;span face=&quot;&quot;&gt;8&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;日（星期五）下午&lt;/strong&gt;&lt;strong&gt;&lt;span face=&quot;&quot;&gt;13&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;：&lt;/strong&gt;&lt;strong&gt;&lt;span face=&quot;&quot;&gt;30&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;地点：图书馆辅楼二楼多媒体电子阅览区&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;strong&gt;主讲人：吴海媛&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;讲座对象：全体师生&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;&quot;&gt;JSTOR全名为Journal Storage,&lt;/span&gt;是以收录西文过刊闻名的全文数据库。&lt;span style=&quot;background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;&quot;&gt;回溯年代最早至1665&lt;/span&gt;年。库中的“最新期刊”多为三至五年前的期刊。具有非常珍贵的学术价值。JSTOR选刊标准十分严格，通过综合评价期刊的订购数量、引用分析、专家推荐以及出版时间等因素来选取相关领域最具有代表性的学术期刊，其中被SCI/SSCI/AHCI 收录的核心期刊达占全部期刊总数的57%。&lt;span style=&quot;background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;&quot;&gt;我馆购买了人文社科的三个专辑。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 讲座内容：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span face=&quot;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 1.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;&quot;&gt;JSTOR&lt;/span&gt;检索界面介绍&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span face=&quot;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;2.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;&quot;&gt;JSTOR&lt;/span&gt;检索功能概览&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span face=&quot;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 3.&amp;nbsp;&lt;/span&gt;应用实例演示和练习&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span face=&quot;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp; 4.&amp;nbsp;&lt;/span&gt;现场交流环节，师生互动提问&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460296472, 1),
(10, 0, 5, '“博雅讲坛”讲座通知：文学与现实', '“反思”和其他的思维方式是需要我们学习的。这一回，文学院的李阳老师以“文学与现实”这个话题为入口，给大家带来不一样的思维方式。', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 18pt; background: white;&quot;&gt;&lt;strong&gt;【&lt;/strong&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, sans-serif;&quot;&gt;&lt;strong&gt;“&lt;/strong&gt;&lt;strong&gt;博雅讲坛&lt;/strong&gt;&lt;strong&gt;”——&lt;/strong&gt;&lt;strong&gt;第一期&lt;/strong&gt;&lt;strong&gt;.&lt;/strong&gt;&lt;strong&gt;第二场】&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 18pt; background: white;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 18pt; background: white;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-family: 隶书, sans-serif;&quot;&gt;&lt;span style=&quot;font-size: 22px;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;strong&gt;文学与现实&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 18pt; background: white;&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 21pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;strong&gt;主讲人:李阳教授&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 21pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;strong&gt;时间：2016年4月13日13:30—15:00（星期三）&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 21pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;strong&gt;地点：图书馆报告厅（主楼103）&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 21pt; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;span style=&quot;font-family: 隶书, sans-serif;&quot;&gt;有时间作一些徒劳的思考，是挺有意思的一件事。在这个放弃了追寻“绝对价值”的时代，人们似乎干什么都可以了，似乎能真正为所欲为了。可是，这样就算自由了吗？&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;span style=&quot;font-family: 隶书, sans-serif;&quot;&gt;我们不妨学着去“反思”。什么是“反思”？对于我们的生活境况而言，就是当我们随波逐流地学习、生活一段时间后，反顾来路，自觉到了我们过去至今随波逐流的状态。对于这世界的诸种经验事实而言，就是将它们“问题化”，意识到很多东西不是可以脱口而出、不言自明的。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;span style=&quot;font-family: 隶书, sans-serif;&quot;&gt;“反思”和其他的思维方式是需要我们学习的。这一回，文学院的李阳老师以“文学与现实”这个话题为入口，给大家带来不一样的思维方式。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460296575, 1),
(11, 0, 10, '失物招领', '失物招领', '&lt;p&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; background-color: rgb(255, 255, 255);&quot;&gt;图书馆失物招领旨在让我们收到的失物尽快回到主人身边，同时友情提醒各位读者爱护图书馆公共财物，妥善保管好个人物品。我们衷心感谢所有拾金不昧者，他们的善行使我们的学习环境更和谐更温馨。&lt;/span&gt;&lt;br style=&quot;color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;/&gt;&lt;span style=&quot;color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 请失物者携带有效证件及时到门卫处领取个人物品。&lt;/span&gt;&lt;/p&gt;', NULL, 0, 1460298008, 1),
(12, 0, 10, '自习室的介绍', '自习室介绍', '&lt;p style=&quot;white-space: normal;&quot;&gt;学校图书馆为学生提供了看书学习的自习室，自习室开放时间为星期一到星期日早上八点到晚上九点四十五&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;（节假日期间自习室开放时间另行通知）&lt;/p&gt;&lt;p style=&quot;white-space: normal;&quot;&gt;图书馆每层都为学生提供自习室，希望大家到自习室学习时保持安静，祝大家学业有成！&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460298159, 1),
(13, 0, 10, '学位论文提交', '学位论文提交', '&lt;p&gt;&amp;nbsp; 关于大家的学位论文提交，请链接到学校官网应用系统， 登录综合服务系统，学生端，按要求提交论文&lt;/p&gt;', NULL, 0, 1460298370, 0),
(14, 0, 10, '学科服务', '学科服务', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&lt;span style=&quot;color: rgb(0, 0, 128);&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size: 20px;&quot;&gt;&lt;span style=&quot;color: rgb(139, 69, 19);&quot;&gt;服务宗旨&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;为了加强为科研服务的力度，我们组建了学科服务团队，&lt;/span&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;通过与各学院建立直接的联系，一方面帮助广大师生充分了解和利用图书馆的资源和服务；另一方面，院系老师对图书馆的各种意见、建议、需求都可以直接反馈给本学院学科馆员，由学科馆员负责沟通、协调、答复和解决问题。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;服务宗旨：&amp;nbsp; 拓宽服务 提升层次 深入学科&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;color: rgb(139, 69, 19);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-size: 20px;&quot;&gt;&lt;span style=&quot;color: rgb(139, 69, 19);&quot;&gt;服务内容&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;1. 学科资源建设&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 了解对口院系的信息需求，联系和组织院系教师、科研人员推荐、选订对口学科的中外文图书、期刊目录及数字资源需求，试用、评介相关学科的数字资源。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;strong&gt;2. 本馆资源、服务推介&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 及时将本学科相关各种中外文资源、各种新进设备及开展的各项服务推介到各院系，以让广大师生充分、及时了解和利用图书馆的资源与服务。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;&amp;nbsp;3. 信息素养教育&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;通过新生入馆教育、数据库资源的讲座培训、嵌入式检索课程等多种方式开展学生的信息素养教育。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;&amp;nbsp;4. 助力教学、科研活动&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;向教师介绍学科资源及检索、使用方法；可以帮着定期检索和推送所需相关文献信息；帮助获取馆外文献；可以嵌入教师课堂承担部分检索课程。努力利用图书馆学专业知识为教学、科研助力。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;strong&gt;&amp;nbsp;5. 个性化信息服务&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 建立对口院系的教师档案，了解相关学术活动，调查教师需求，开展个性化的信息服务。搜集、鉴别和整理相关学科的网络信息资源，参与相关学科文献资源的分析、评价工作。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460298435, 1);
INSERT INTO `lib_article` (`id`, `parse`, `type`, `title`, `summary`, `content`, `template`, `bookmark`, `createtime`, `status`) VALUES
(15, 0, 10, '常见的问题', '常见的问题', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;1.如何在图书馆借书？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 使用本人一卡通办理，办理方式有两种：人工和自助。人工办理请到一楼总服务台，或二、三楼的分服务台（位于二、三楼的南北两侧）；自助办理可使用自助借还设备，主楼设有四台自助借还机，一楼一台，位于总服务台旁，二楼两台，位于西区南北两侧、三楼一台，位于西区北侧。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;2.如何还书？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 办理方式有两种：人工和自助。人工办理请到一楼总服务台，或二、三楼的分服务台（位于二、三楼的南北两侧）；自助办理可使用自助借还设备，主楼设有四台自助借还机，一楼一台，位于总服务台旁，二楼两台，位于西区南北两侧、三楼一台，位于西区北侧。另外图书馆在主楼正门左侧设有24小时自助还书机，方便读者自助还书。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;3.图书丢失、损毁了怎么办？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 读者应爱护图书，如遗失或损坏图书，必须进行赔偿。具体规定如下：&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; （一） 遗失赔偿&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 1、读者严重损坏图书或遗失图书，可以购买书名、著者、出版社相同的版本或该书新版的新书赔偿（赔书后找到原书不予退赔），否则须按以下规定赔偿：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;⑴一般中文图书按书原价5倍赔偿，小说按3倍赔偿，计算机类图书出版时间超过三年的（含三 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;年），按2倍赔偿，不足三年的，按3倍赔偿。图书原价不足5元的，均按原价5元计算赔偿。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;⑵丢失成套书刊中的一册，按成套书刊价格的5倍赔偿。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;⑶外文原版书按20倍赔偿。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; ⑷损毁线装书、善本书等特许借阅的图书按30倍赔偿。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;⑸因图书丢失引起的图书超期，均按超期交滞纳金的规定处理。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;2、遗失单张报纸者，按全月合订本价格赔偿，遗失合订本报纸者，按合订本价格的5~10倍赔偿。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27.1pt;&quot;&gt;3、损坏图书、期刊视情节支付赔偿：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; margin-left: -0.1pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 27.1pt; background-color: rgb(255, 255, 255);&quot;&gt;⑴损坏图书、期刊程度较轻者，不影响书刊内容完整的，按书刊价格的20%~1倍赔偿。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; margin-left: -0.1pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 27.1pt; background-color: rgb(255, 255, 255);&quot;&gt;⑵损坏书刊较严重者，必须经过加工整修方能流通者，按原价赔偿，并付加工费。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; margin-left: -0.1pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; text-indent: 27.1pt; background-color: rgb(255, 255, 255);&quot;&gt;⑶读者剪裁、撕割书刊者，一律按偷书处理。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; （二）关于偷盗书刊的规定&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;图书资料是国家财产，广大读者应与爱护，图书馆是公共场所，广大读者应遵守有关制度、规定。为维护公共财产，堵塞漏洞，防止偷盗、损坏书刊文献资料的事件发生，保证教学、科研工作的顺利进行，对违章、违纪读者处理，特作如下规定：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;1&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;、凡窃书者，一律予以罚款，张榜示名，取消借阅资格，并根据情节轻重，给予行政纪律处分。窃书者处以原书、刊价格十倍以上罚款，少于&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;元的，一律按&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;元计算。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;2&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;、拆除书中磁性资料者，其性质与窃书相同，拆除一册书的磁性材料罚款&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;30&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;元。拆除书中磁性材料并窃走者，按本条与&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;1&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;条同时处罚，最少罚款&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;80&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;3&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;、损坏书中计算机借阅用条码者，每条罚款&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;2&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;4&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;、一卡通限本人使用，严禁转借，一经发现应批评教育。因转借所产生的一切后果均由原领证人负责。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;5、对用他人遗失的一卡通冒借图书者，一经查出，均按窃书论处。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;6、凡借口马虎将图书带出监视器进口或将期刊带出阅览室者，一律按违章处理，处&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;以书、刊原价的10倍以上罚款，低于5元的按5元计价。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 7、对不遵守入库及阅览室规则者，图书馆应批评教育，对其中不接受教育者，视情节轻重，令其停止入库、入馆借阅图书一个月。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;8、对检举各种违纪、违章行为者，给予适当奖励。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;如何办理图书报销登记？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;办理时间：每周三上午8:00--11:30&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;办理地点：图书馆主楼一楼 采编部123房间&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;需携带的资料：《教学、行政及科研部门校有图书等文献资料》登记表一式两份，有效发票。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;注意事项：1.请老师们事先按要求填好登记表（最好统计出图书的种数及册数）。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 2.只要手续齐全，可委托其他人代办。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;4.博雅厅收藏了什么内容？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 收藏了两部分内容，一是东芝公司、金沢市捐赠的日文原版图书；二是我馆自建的连大学人文库，收藏了我校教师和往届学子的学术论著和作品。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&amp;nbsp;5.图书馆的书是按什么顺序排架的？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;【1】图书按索书号先后顺序排架。索书号由分类号、著者号及附加号组成。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;【2】所有图书先按分类号的大小排列，小号在前，大号在后。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;【3】同类图书再按著者号的顺序排列，先按拉丁字母（中、西、日文）或俄文字母顺序排列，再按阿拉伯数字大小排列。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;【4】同类号、同著者号的不同图书，再按附加号的顺序排列：双字母附加号排在单字母附加号之后。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;【5】同种书的不同版本，按版次的顺序倒排，即第二版排在第一版的前面，依此类推。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;【6】多卷。多届次的同种书，按 卷次、届次或年代的顺序排列。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;【7】多卷册的各种版本，按同一版本的卷册集中排列。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;6.如何办理续借？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 续借应在图书到期前10天内进行，续借期从续借日算起，过期图书不接受续借。若有超期未还图书或欠款，不得继续借书，直到还清所有超期图书和滞纳金 。续借有两种方式：&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 一是在图书馆总服务台办理；二是登录图书馆主页，点击“馆藏书目检索”---“我的图书馆”---输入用户名、密码、验证码，登录---“当前借阅”---点击相关图书右侧的“续借”按键即可。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;7.如果所借图书到期日正好在假期怎么办？会超期吗？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 在国家法定节假日（公休日除外）期间内到期的图书，可在节后开放5日内归还；在寒暑假期间到期的图书，可在开学后三周内归还，其余均按到期日归还，借阅期限均含假期在内。如有特殊情况，所借的图书无论到期与否本馆有权追回。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;8.如果发现有偷书或损坏磁条之类的行为会如何处罚？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 图书资料是国家财产，广大读者应与爱护，图书馆是公共场所，广大读者应遵守有关制度、规定。为维护公共财产，堵塞漏洞，防止偷盗、损坏书刊文献资料的事件发生，保证教学、科研工作的顺利进行，对违章、违纪读者处理，特作如下规定：&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; （1）凡窃书者，一律予以罚款，张榜示名，取消借阅资格，并根据情节轻重，给予行政纪律处分。窃书者处以原书、刊价格十倍以上罚款，少于5元的，一律按5元计算。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;（2）拆除书中磁性资料者，其性质与窃书相同，拆除一册书的磁性材料罚款30元。拆除书中磁性材料并窃走者，按本条与1条同时处罚，最少罚款80元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;（3）损坏书中计算机借阅用条码者，每条罚款2元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;（4）一卡通限本人使用，严禁转借，一经发现应批评教育。因转借所产生的一切后果均由原领证人负责。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;（5）对用他人遗失的一卡通冒借图书者，一经查出，均按窃书论处。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;（6）凡借口马虎将图书带出监视器进口或将期刊带出阅览室者，一律按违章处理，处以书、刊原价的10倍以上罚款，低于5元的按5元计价。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;（7）对不遵守入库及阅览室规则者，图书馆应批评教育，对其中不接受教育者，视情节轻重，令其停止入库、入馆借阅图书一个月。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;（8）对检举各种违纪、违章行为者，给予适当奖励。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&lt;span style=&quot;text-indent: 27pt;&quot;&gt;9.各类读者利用图书馆的权限及超期滞纳金规定&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;table align=&quot;center&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;650&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;height: 20px;&quot; class=&quot;firstRow&quot;&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-width: 1pt; border-color: windowtext; vertical-align: bottom;&quot; width=&quot;67&quot; height=&quot;20&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;读者类型&lt;/p&gt;&lt;p style=&quot;text-align:center;&quot;&gt;代码&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-left-style: none; border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1pt; border-right-width: 1pt; border-bottom-width: 1pt; vertical-align: bottom;&quot; width=&quot;85&quot; height=&quot;20&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;读者类型&lt;/p&gt;&lt;p style=&quot;text-align:center;&quot;&gt;名称&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-left-style: none; border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1pt; border-right-width: 1pt; border-bottom-width: 1pt; vertical-align: bottom;&quot; width=&quot;85&quot; height=&quot;20&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;借阅&lt;/p&gt;&lt;p style=&quot;text-align:center;&quot;&gt;最大册数&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-left-style: none; border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1pt; border-right-width: 1pt; border-bottom-width: 1pt; vertical-align: bottom;&quot; width=&quot;57&quot; height=&quot;20&quot;&gt;&lt;p&gt;借期（月）&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-left-style: none; border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1pt; border-right-width: 1pt; border-bottom-width: 1pt; vertical-align: bottom;&quot; width=&quot;57&quot; height=&quot;20&quot;&gt;&lt;p&gt;续借次数&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-left-style: none; border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1pt; border-right-width: 1pt; border-bottom-width: 1pt; vertical-align: bottom;&quot; width=&quot;113&quot; height=&quot;20&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;超期罚款&lt;/p&gt;&lt;p style=&quot;text-align:center;&quot;&gt;（元/天）&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0cm; margin: 0px; border-left-style: none; border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1pt; border-right-width: 1pt; border-bottom-width: 1pt; vertical-align: top;&quot; width=&quot;186&quot; height=&quot;20&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;备注&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 35px;&quot;&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-right-color: windowtext; border-bottom-color: windowtext; border-left-color: windowtext; border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt; vertical-align: bottom;&quot; width=&quot;67&quot; height=&quot;35&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;01&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;85&quot; height=&quot;35&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;本科生&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;85&quot; height=&quot;35&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;10&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;57&quot; height=&quot;35&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;1&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;57&quot; height=&quot;35&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;2&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt;&quot; width=&quot;113&quot; height=&quot;35&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;0.10&lt;/p&gt;&lt;/td&gt;&lt;td rowspan=&quot;3&quot; style=&quot;padding: 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: top;&quot; width=&quot;186&quot; height=&quot;35&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;其中文学类图书（I大类），本科生限借两册，且不能续借；文学院学生不限类别。&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 48px;&quot;&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-right-color: windowtext; border-bottom-color: windowtext; border-left-color: windowtext; border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt; vertical-align: bottom;&quot; width=&quot;67&quot; height=&quot;48&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;02&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;85&quot; height=&quot;48&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;硕士&lt;/p&gt;&lt;p style=&quot;text-align:center;&quot;&gt;研究生&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;85&quot; height=&quot;48&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;20&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;57&quot; height=&quot;48&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;2&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;57&quot; height=&quot;48&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;2&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt;&quot; width=&quot;113&quot; height=&quot;48&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;0.10&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 39px;&quot;&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-right-color: windowtext; border-bottom-color: windowtext; border-left-color: windowtext; border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt; vertical-align: bottom;&quot; width=&quot;67&quot; height=&quot;39&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;03&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;85&quot; height=&quot;39&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;教工&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;85&quot; height=&quot;39&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;30&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;57&quot; height=&quot;39&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;2&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt; vertical-align: bottom;&quot; width=&quot;57&quot; height=&quot;39&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;5&lt;/p&gt;&lt;/td&gt;&lt;td nowrap=&quot;nowrap&quot; style=&quot;padding: 0.75pt 0.75pt 0cm; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1pt; border-right-color: windowtext; border-right-width: 1pt;&quot; width=&quot;113&quot; height=&quot;39&quot;&gt;&lt;p style=&quot;text-align:center;&quot;&gt;0.10&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;10.图书馆开馆时间&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; text-indent: 10.5pt; line-height: 21px; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; 1、主楼开馆时间：周一至周五：早8：10，周六、日：早8：40&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; text-indent: 10.5pt; line-height: 21px; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; 2、辅楼开馆时间：周一至周日：早6：10&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; text-indent: 10.5pt; line-height: 21px; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; 3、主楼、辅楼闭馆时间：周一至周日：21：40&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;11.什么是分类号？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 我馆图书根据中图法即中国图书分类法分类&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;quot;中国图书分类法&amp;quot;是在科学分类的基础上，结合图书的特性所编制的分类法。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;line-height: 18pt; text-indent: 24pt;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 它将学科分五基础类，22个大类，基本序列是：马列毛思想、哲学、社会科学、自然科学、综合性图书，&lt;/span&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;735&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;height: 22px;&quot; class=&quot;firstRow&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-width: 1pt; border-color: rgb(230, 230, 230);&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;A&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;马克思主义、列宁主义、毛泽东思想&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-left-style: none; border-top-color: rgb(230, 230, 230); border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-top-width: 1pt; border-right-width: 1pt; border-bottom-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;N&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;自然科学总论&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 22px;&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-left-color: rgb(230, 230, 230); border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;B&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;哲学&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: rgb(230, 230, 230); border-bottom-width: 1pt; border-right-color: rgb(230, 230, 230); border-right-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;O&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;数理科学和化学&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 22px;&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-left-color: rgb(230, 230, 230); border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;C&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;社会科学总论&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: rgb(230, 230, 230); border-bottom-width: 1pt; border-right-color: rgb(230, 230, 230); border-right-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;P&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;天文学、地球科学&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 22px;&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-left-color: rgb(230, 230, 230); border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;D&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;政治、法律&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: rgb(230, 230, 230); border-bottom-width: 1pt; border-right-color: rgb(230, 230, 230); border-right-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;Q&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;生物科学&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 22px;&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-left-color: rgb(230, 230, 230); border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;E&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;军事&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: rgb(230, 230, 230); border-bottom-width: 1pt; border-right-color: rgb(230, 230, 230); border-right-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;R&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;医药、卫生&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 22px;&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-left-color: rgb(230, 230, 230); border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;F&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;经济&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: rgb(230, 230, 230); border-bottom-width: 1pt; border-right-color: rgb(230, 230, 230); border-right-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;S&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;农业科学&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 22px;&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-left-color: rgb(230, 230, 230); border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;G&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;文化、科学、教育、体育&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: rgb(230, 230, 230); border-bottom-width: 1pt; border-right-color: rgb(230, 230, 230); border-right-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;T&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;工业技术&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 22px;&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-left-color: rgb(230, 230, 230); border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;H&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;语言、文字&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: rgb(230, 230, 230); border-bottom-width: 1pt; border-right-color: rgb(230, 230, 230); border-right-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;U&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;交通运输&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 22px;&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-left-color: rgb(230, 230, 230); border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;I&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;文学&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: rgb(230, 230, 230); border-bottom-width: 1pt; border-right-color: rgb(230, 230, 230); border-right-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;V&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;航空、航天&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 22px;&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-left-color: rgb(230, 230, 230); border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;J&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;艺术&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: rgb(230, 230, 230); border-bottom-width: 1pt; border-right-color: rgb(230, 230, 230); border-right-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;X&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;环境科学&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 22px;&quot;&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-right-color: rgb(230, 230, 230); border-bottom-color: rgb(230, 230, 230); border-left-color: rgb(230, 230, 230); border-right-width: 1pt; border-bottom-width: 1pt; border-left-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;K&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;历史、地理&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;padding: 1.5pt 7.5pt; margin: 0px; border-top-style: none; border-left-style: none; border-bottom-color: rgb(230, 230, 230); border-bottom-width: 1pt; border-right-color: rgb(230, 230, 230); border-right-width: 1pt;&quot; height=&quot;22&quot;&gt;&lt;p style=&quot;text-align:left;line-height: 18pt;&quot;&gt;&lt;strong&gt;Z&amp;nbsp;&lt;/strong&gt;&lt;strong&gt;综合性图书&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 中国图书馆分类法共分5个基本部类、22个大类。采用&lt;a href=&quot;http://baike.baidu.com/view/104417.htm&quot; target=&quot;_blank&quot; style=&quot;color: rgb(85, 85, 85); text-decoration: none; outline: none;&quot;&gt;汉语拼音字母&lt;/a&gt;与阿拉伯数字相结合的混合号码，用一个字母代表一个大类，以字母顺序反映大类的次序，在字母后用数字作&lt;a href=&quot;http://baike.baidu.com/view/119353.htm&quot; target=&quot;_blank&quot; style=&quot;color: rgb(85, 85, 85); text-decoration: none; outline: none;&quot;&gt;标记&lt;/a&gt;。为适应工业技术发展及该类文献的分类，对工业技术二级类目，采用双字母。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;12.图书馆都有哪些文献资源？&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;我馆现有纸质资源、电子资源和特色资源三大类文献资源&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 纸质资源&lt;strong&gt;：&lt;/strong&gt;图书&amp;nbsp; 127万册&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;期刊&amp;nbsp; 763种&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;报纸&amp;nbsp; 114种&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 数字资源&lt;strong&gt;：&lt;/strong&gt;电子图书&amp;nbsp;&amp;nbsp; 34万册&lt;/p&gt;&lt;p&gt;&amp;lt;p', NULL, 0, 1460298543, 1);
INSERT INTO `lib_article` (`id`, `parse`, `type`, `title`, `summary`, `content`, `template`, `bookmark`, `createtime`, `status`) VALUES
(16, 0, 10, '阅读推荐', '阅读推荐', '&lt;p&gt;红楼梦&lt;/p&gt;&lt;ul class=&quot;book-wrapper list-paddingleft-2&quot; style=&quot;list-style-type: none;&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;a href=&quot;http://localhost/think-library/index.php#&quot; class=&quot;brown&quot; style=&quot;text-decoration: none; display: block; width: 150px; color: rgb(85, 85, 85);&quot;&gt;&lt;img src=&quot;http://localhost/think-library./Application/Home/View/workshop/images/book1.jpg&quot;/&gt;红高粱&lt;/a&gt;&lt;span style=&quot;color: rgb(136, 136, 136);&quot;&gt;莫言作品&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;a href=&quot;http://localhost/think-library/index.php&quot; class=&quot;brown&quot; style=&quot;text-decoration: none; display: block; width: 150px; color: rgb(85, 85, 85);&quot;&gt;&lt;img src=&quot;http://localhost/think-library./Application/Home/View/workshop/images/book2.png&quot;/&gt;平凡的世界&lt;/a&gt;&lt;span style=&quot;color: rgb(136, 136, 136);&quot;&gt;路遥作品&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460305008, 1),
(17, 0, 10, 'VPN服务', 'VPN服务', '&lt;p&gt;VPN服务 需要在图书馆使用相应的证件办理。&lt;/p&gt;', NULL, 0, 1460305204, 1),
(18, 0, 12, '本馆介绍', '历史：\r\n我馆是于1995年由原大连大学工学院图书馆、大连大学医学院图书馆及大连大学师范学院图书馆合并而成。\r\n面积：\r\n图书馆大楼于1995年建成并使用，总建筑面积21550.6平方米。\r\n借阅：\r\n馆内图书分藏五个库室，均实行开架借阅。馆内有15个阅览室，其中电子阅览室和多媒体阅览室拥有110个阅览座位。另有设在各', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;一、图书馆整体布局与功能划分&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;大连大学图书馆分主楼和辅楼两部分。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; （一）图书馆主楼&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;图书馆主楼建筑面积为2.1万平方米，楼体共有五层。负一楼为密集书库，可藏书200余万册，目前藏三线库图书60余万册；一楼为图书馆大厅，拥有总服务台、信息发布机、电子查询机、电子图书借阅机、读报机等各种电子设施；同时拥有自主学习区和新书阅览区，阅览座位共 192个，休闲座位共 64个，一楼西侧设自主学习研修室8个，供全校教师和学生研究性学习使用；另有学术报告厅1个，会议室1个。二楼主要为藏书区和阅览区，东区所藏图书为马列、哲学、社会科学、政治、军事、经济、教育7大类；西区所藏图书为自然科学总论、数理化、地球科学、生物、医学、农业、交通运输、航空、环境等9大类；南区所藏图书为语言文字类；各区均提供普通阅览座位和休闲阅览座位，共有普通阅览座位782 个，休闲座位188 个，有声空间4个；二楼东北角设有读者服务中心，为读者提供图书、纸、笔、本、书签等文化用品，以及简单的包装食品及饮品。三楼同样为藏书区和阅览区，其中东区所藏图书为中国文学类；西区所藏图书为工业技术和综合类；南区所藏图书为历史地理类；北区所藏图书为外国文学、艺术类 。该楼层共有普通阅览座位 462个，休闲座位 185个，有声空间4个。四楼为古籍阅览室和教师阅览室，所藏古籍2万余册，教师阅览室藏各学科学术前沿性新书3万余册，供教师阅览。该楼层共有阅览座位104 个。此外，为方便到馆读者，各楼层均设有自助借还书系统，自助文印系统等。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;1—3层东南角为图书馆办公区，其中一楼为参考咨询部办公室；二楼为图书馆办公室和馆长、副馆长室；三楼为流通部办公室以及书记室、副馆长室。采编部办公室设在一楼西南角。&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 10pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 18pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;（二）图书馆辅楼&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 10pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 18pt; background-color: rgb(255, 255, 255);&quot;&gt;图书馆辅楼建筑面积为1.06万平方米，其中负一楼和一楼东侧为档案馆，其余部分为图书馆，主要用于师生的自习、报刊阅览和研讨交流。二楼东侧为期刊阅览区，二楼西侧为报纸阅览区和数字阅览区；三楼、四楼整层和五楼的西侧均为学生自习区；五楼东侧为学术交流研讨室。&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 10pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 18pt; background-color: rgb(255, 255, 255);&quot;&gt;图书馆辅楼主要的功能有：期刊阅览、报纸阅览和电子阅览，此外最重要的功能是为学生提供自习场所，共有普通自习、阅览座位1544个；电子阅览座位120个；休闲座位168个；交流研讨室12间，座位760个。此外，为方便读者，在二楼回廊处设有休憩小站，为师生提供学习之余的小憩之地。&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 10pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; background-color: rgb(255, 255, 255);&quot;&gt;五楼的12间学术交流研讨室，作为班级、团队、协会、社团、部门和研讨小组等各种团体开展学习交流与研讨的场所，面向全校师生开放。&lt;/p&gt;&lt;p style=&quot;text-align:left;padding: 0px; margin: 0cm 0cm 10pt; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 30.1pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;二、&lt;/strong&gt;&lt;strong&gt;图书馆馆藏资源&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;大连大学图书馆目前馆藏资源包括纸本资源、数字资源和特色资源三大类。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;（一）纸质资源：包括纸质图书、期刊和报纸。图书为127万册，期刊为763种，报纸114种；&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;（二）数字资源：包括电子图书、期刊、数据库。拥有电子图书34万册，中外文电子期刊 1万余种，中文数据库17种和外文数据库15种；&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;（三）特色资源：图书馆特色资源包括古籍资源2万余册；校本资源是正在构建的“连大学人文库”。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;目前馆藏资源涵盖哲学、法学、经济学、教育学、文学、艺术、历史、理学、工学、医学、管理学、农业、军事等所有的一级学科门类。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 30px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 三、图书馆信息化设施&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24.1pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;（一）&lt;/strong&gt;图书馆的网络交换系统采用有线无线一体化的设计，无线网络覆盖整个图书馆，使有线无线互为补充。图书馆网络交换系统拟采用核心层和接入层二层体系架构，核心层采用万兆核心路由交换机；接入层全部采用千兆智能安全交换机进行接入控制，接入交换机分别通过万兆链路连接到汇聚交换机。从而实现千兆到工作桌面的访问速度。接入交换机上百台，链接有线和无线网络、电话线、监控节点、有线电视约2300个节点。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;在无线建设方面采用大容量高性能的基于802.11n协议的无线接入设备上行接口采用千兆以太网接口接入，实现无线多媒体应用成为现实。无线交换机采用全千兆三层交换，采用POE供电，简化线路敷设。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;整网支持IPv6，并采用华为智能管理中心进行全网设备的统一管理，实现了有线无线的一体化。满足师生对局域网的使用需求，同时便于网络管理和维护。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24.1pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;（二）&lt;/strong&gt;核心机房和灾备机房参照B类机房设计标准，总面积约300平，主设备区约150平，可容纳20个机柜，建设工程包括机房装修、接地系统、配电系统、精密空调、门禁及环境监控系统、气体消防系统等。配备3套恒温恒湿空调，2套UPS配电系统，1套环境监控系统、2套气体消防系统。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24.1pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;（三）&lt;/strong&gt;现有服务器15台，网络存储2套，采用VMWARE服务器虚拟化技术，通过服务器虚拟化软件，构建后台服务器虚拟化池，对服务器资源进行整合，并为图书馆自动化系统的各种应用重新动态分配服务器资源，从而以一种非常经济的方式达到扩展或者增加服务器资源的目的。从而减少了物理服务器的使用数量，既显著降低资金成本和运营成本，又能保证图书馆各类应用系统运行的灵活性，而且易于管理和维护，达到绿色节能的目标。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24.1pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;（四）&lt;/strong&gt;更换全部纸质图书磁条，采用RFID+磁条的模式，配备2套5通道门禁系统，3套图书监测系统，2台RFID推车式盘点机，7台RFID馆员工作站,按岗位设置图书馆员业务电脑50台。业务自动化仍沿用汇文文献信息服务系统，该系统是图书馆馆藏文献的综合管理系统，是图书馆基于馆藏资源的所有服务的基础系统，主要实现文献的采访、编目、流通、OPAC检索等功能。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24.1pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;（五）&lt;/strong&gt;形成以大流通为主的新的借还模式，创新服务手段，大力发展学生自助服务。自助借还书机3台、24小时还书机1台、触摸屏阅报机4台，自助电子书借阅机3台，1套联创自助文印系统，配置3台复印机，检索机24台。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;主楼大厅正面及侧面设计室内全彩LED显示屏二块，用于信息发布以及多媒体视频的展示，辅楼正厅设置一块条屏，显示屏采用同步显示卡，基于内部局域网即可完成信息及视频的上传及展示工作。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24pt; background-color: rgb(255, 255, 255);&quot;&gt;主楼和辅楼共设置壁挂式发布机10块，立式发布机10块，采用网络传输方式集中控制，及时向读者发布各类信息。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24.1pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;（六）&lt;/strong&gt;建设超过300平的信息共享空间，设置120台电脑，其中以自主浏览和检索培训为主，为读者提供及时、快捷和大容量信息的文献信息服务场所。为读者提供电子图书阅读、各类信息查询、网页浏览、影音视听、远程教育、自助培训等以数字查阅为主的信息化服务，并开展科技检索、数据库讲座及培训等延伸服务。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24.1pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;（七）&lt;/strong&gt;建设12个学生活动区（辅楼5楼）配备投影仪，移动音响，6个研修室配备网络电视，移动电子白板。1个学术报告厅，2个学术交流室，配备专业的音响及投影设备。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; color: rgb(85, 85, 85); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; line-height: 21px; text-indent: 24.1pt; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;（八）&lt;/strong&gt;升级图书馆网站建设平台，定制网站PC模板3套，PAD模板1套，手机模板1套。加入CALIS图书馆网上虚拟咨询系统和文献传递系统，为读者开展电子邮件咨询、实时在线咨询等服务，实现读者校外VPN访问。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460306867, 1),
(19, 0, 12, '分布图示', '分布图示', '&lt;p&gt;分布图示&lt;br/&gt;&lt;/p&gt;&lt;h3 class=&quot;on&quot; style=&quot;padding: 0px 0px 0px 25px; margin: 0px; font-weight: normal; font-size: 18px; text-align: left;&quot;&gt;&lt;a href=&quot;http://ldlu.dlu.edu.cn/Category_1108/Index.aspx&quot; target=&quot;_blank&quot; style=&quot;color: rgb(51, 51, 51); text-decoration: none; outline: none;&quot;&gt;主楼&lt;/a&gt;&lt;/h3&gt;&lt;ul class=&quot;picListFl list-paddingleft-2&quot; style=&quot;list-style-type: none;&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;img src=&quot;/think-library/Uploads/ueditor/image/20160418/5714c6a01c421.jpg&quot; title=&quot;5714c6a01c421.jpg&quot; alt=&quot;2mu28m36bl.jpg&quot; width=&quot;281&quot; height=&quot;243&quot; style=&quot;width: 281px; height: 243px;&quot;/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;图书馆主楼分布图示&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;', NULL, 0, 1460306950, 1),
(20, 0, 12, '馆舍风貌', '馆舍风貌', '&lt;p&gt;馆舍风&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 18px;&quot;&gt;&amp;nbsp; 主楼内部&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;img src=&quot;/think-library/Uploads/ueditor/image/20160418/5714c732d663b.jpg&quot; title=&quot;5714c732d663b.jpg&quot; alt=&quot;ac4bd11373f08202bbeea87049fbfbedab641b04.jpg&quot; width=&quot;235&quot; height=&quot;135&quot; style=&quot;width: 235px; height: 135px;&quot;/&gt;&lt;br/&gt;&lt;/p&gt;&lt;h3 class=&quot;on&quot; style=&quot;padding: 0px 0px 0px 25px; margin: 0px; font-weight: normal; font-size: 18px; text-align: left;&quot;&gt;&lt;a href=&quot;http://ldlu.dlu.edu.cn/Category_1107/Index.aspx&quot; target=&quot;_blank&quot; style=&quot;color: rgb(51, 51, 51); text-decoration: none; outline: none;&quot;&gt;辅楼&lt;/a&gt;内部&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &lt;a title=&quot;辅楼外部&quot; href=&quot;http://ldlu.dlu.edu.cn/Item/2731.aspx&quot; target=&quot;_blank&quot; style=&quot;white-space: normal; color: rgb(102, 102, 102); text-decoration: none; outline: none; font-size: 14px;&quot;&gt;辅楼内部&lt;/a&gt;&lt;/h3&gt;&lt;p&gt;&lt;img src=&quot;/think-library/Uploads/ueditor/image/20160418/5714c747adaa3.jpg&quot; title=&quot;5714c747adaa3.jpg&quot; alt=&quot;内部.jpg&quot; width=&quot;270&quot; height=&quot;144&quot; style=&quot;width: 270px; height: 144px;&quot;/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;img src=&quot;/think-library/Uploads/ueditor/image/20160418/5714c758a904e.jpg&quot; title=&quot;5714c758a904e.jpg&quot; alt=&quot;图书馆一角.jpg&quot; width=&quot;235&quot; height=&quot;141&quot; style=&quot;width: 235px; height: 141px;&quot;/&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460307029, 1),
(21, 0, 9, '图书馆征集“大学人文库”文献资料的通知', '为了更好地反映大学的教学、科研成果，珍藏、展示和传播我校师生和校友的学术著作，弘扬大学', '&lt;p&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 12px; line-height: 20px; text-indent: 24px; background-color: rgb(255, 255, 255);&quot;&gt;为了更好地反映大学的教学、科研成果，珍藏、展示和传播我校师生和校友的学术著作，弘扬大连大学风气，开展图书馆征集“大学人文库”文献资料&lt;/span&gt;&lt;/p&gt;', NULL, 0, 1460307118, 1),
(22, 0, 9, '图书馆校外访问系统成功上线', '图书馆校外访问系统成功上线', '&lt;p&gt;&lt;span style=&quot;color: rgb(102, 102, 102); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; font-size: 12px; line-height: 20px; text-indent: 24px; background-color: rgb(255, 255, 255);&quot;&gt;尊敬的领导，亲爱的老师同学们，为全校师生提供文献资源服务是图书馆的核心工作，为了使图书馆所&lt;/span&gt;&lt;/p&gt;', NULL, 0, 1460307225, 1),
(23, 0, 12, '部门设置', '部门设置', '&lt;p&gt;&lt;a href=&quot;http://localhost/think-library/index.php#&quot; class=&quot;notice-title&quot; style=&quot;text-decoration: none; display: block; height: 15px; margin-bottom: 5px; font-size: 13px; font-weight: bold; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; white-space: normal;&quot;&gt;部门设置&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; font-size: 11px; line-height: 20px; color: rgb(153, 153, 153); text-indent: 24px; font-family: &amp;#39;Microsoft Yahei&amp;#39;, SimSun, Tahoma, Helvetica, Arial, sans-serif; white-space: normal;&quot;&gt;大连大学图书馆部门设置为一室五部：办公室、采编部、阅览部、流通部、参考咨询部、信息部。 1、 办公室 （电话：87402853、内线62853） 负责全馆日常事务及管理等。 2、采编部 （电话：87402850、内线62850） 采编部负责除期刊以&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', NULL, 0, 1460307390, 1);

