CREATE TABLE IF NOT EXISTS `lib_articletype` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '区域ID',
  `parentid` int(11) NOT NULL COMMENT '上级ID',
  `name` varchar(20) DEFAULT NULL COMMENT '名字',
  `type` tinyint(4) NOT NULL DEFAULT '1' COMMENT '类型',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '状态 1：显示  0:删除',
  PRIMARY KEY (`id`),
  KEY `isShow` (`status`) USING BTREE,
  KEY `areaType` (`type`) USING BTREE,
  KEY `parentId` (`parentid`) USING BTREE
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='文章类型表' AUTO_INCREMENT=13 ;

