CREATE TABLE IF NOT EXISTS `lib_bookinfo` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `isbn` varchar(30) NOT NULL COMMENT 'ISBN码',
  `bookname` varchar(50) NOT NULL COMMENT '图书名字',
  `typeid` int(11) NOT NULL COMMENT '分类',
  `author` varchar(25) NOT NULL COMMENT '作者',
  `publisherid` int(11) NOT NULL COMMENT '出版社',
  `frontcover` int(11) NOT NULL COMMENT '封面',
  `page` int(11) NOT NULL COMMENT '页数',
  `sumnum` int(11) unsigned DEFAULT '1' COMMENT '数量',
  `createtime` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `updatetime` int(11) NOT NULL COMMENT '更新时间',
  `status` tinyint(4) NOT NULL COMMENT '状态： 0 :下架  1:正常 ',
  `bookcaseid` int(11) DEFAULT NULL COMMENT '书柜',
  `remark` varchar(30) DEFAULT NULL COMMENT '备注',
  `nownum` int(11) DEFAULT '0' COMMENT '借出本数',
  PRIMARY KEY (`id`),
  KEY `isbn` (`isbn`) USING BTREE
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='图书信息表' AUTO_INCREMENT=9 ;

